const Edit = ({ add }) => {

    const [note, setNote] = useState("")
    const [date, setDate] = useState("")
    const [time, setTime] = useState("")

    function addItem() {
        add([1,2,3])
    }

    return <div className="edit">
        <h1>Notes</h1>
        <p>Content: </p>
        <input type="text" defaultValue={note}/>
        <p>Date: </p>
        <input type="date"/>
        <p>Time: </p>
        <input type="time"/>
        <button onClick={addItem} className="add">Add</button>
    </div>
}

export default Edit
